/* MoaIncludeDAO.pageHeader */
SELECT  list.*
  FROM  (
				SELECT all_list.*,
						   @rownum := @rownum + 1 AS RNUM
				  FROM (

		
		/* MoaIncludeDAO.pageFooter */
									) all_list,
                               		(SELECT @rownum:=0) R
					 ) list
		 WHERE  list.rnum  > $firstIndex$
			 AND  list.rnum <= $firstIndex$ + $pageUnit$
